#include<iostream>
#include<algorithm>
#include<cmath>
using namespace std;


int main() { 
int n,k;
cin>>n;
long long int candies[n];
cin>>k;
for(int i=0;i<n;++i) cin>>candies[i];
sort(candies,candies+n);
//if(n==1)  { cout<<"0"<<endl; return 0; }
if(k==1) cout<<"0"<<endl;
else {
//for(int i=0;i<n;++i) cout<<candies[i]<<" ";
//cout<<abs(candies[k-1]-candies[0])<;
long long int temp,min=1000000000;
for(int i=0;i<n;++i) {
if((i+(k-1))>(n-1)) break;
temp=abs(candies[i+k-1]-candies[i]);
if(temp<=min) min=temp;
	
}
cout<<min<<endl;
}

return 0;
}
